import React, {Component} from 'react';

class MovieRow extends Component {
    viewMovie(){
        console.log(this.props.movie.title);
        const url = "https://www.themoviedb.org/movie" + this.props.movie.title
        window.loaction.href = url;
    }

    render() {
       return(
        <table key={this.props.movie.id}>
        <tbody>
            <tr>
                <td>
                    <img src={this.props.movie.poster_src} alt="poster" width="120"/>
                </td>
                <td>
                    <h3 style={textStyle}>{this.props.movie.title}</h3>
                    <p style={textStyle}>{this.props.movie.overview}</p>
                    <button style={{
                        float : "left",
                        textAlign  : "justify",
                        marginLeft : "10px",
                        borderRadius : "15%",
                        backgroundColor : "green",
                        color: "white",
                    }} onClick={this.viewMovie.bind(this)}>View</button>
                </td>
            </tr>
        </tbody>
    </table>
       );
    }
}

var textStyle = {
    textAlign : "justify",
    paddingLeft: "10px",
}

export default MovieRow;
